import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { AppStatusService } from 'src/app-status.service';

@Component({
  selector: 'app-application-status',
  templateUrl: './app-status.component.html',
  styleUrls: ['./app-status.component.css']
})
export class AppStatusComponent {
  applicationNo: string = '';
  reportData: any[] = [];

  constructor(private http: HttpClient, private appStatusService: AppStatusService) { }

  filterReport() {
    this.appStatusService.getReport(this.applicationNo).subscribe(data => {
      this.reportData = data;
    }, error => {
      console.error('Error fetching report data', error);
    });
  }
}
