import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class AppStatusService {

  private apiUrl = 'http://localhost:3000/api/report';

  constructor() { }

  // Method to fetch the report data based on applicationNo using fetch
  async getReport(applicationNo: string): Promise<any[]> {
    try {
      const response = await fetch(`${this.apiUrl}?applicationNo=${applicationNo}`);

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      return data;
    } catch (error) {
      console.error('Error fetching report data:', error);
      return [];
    }
  }
}
