import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { ReportService } from '../report.service';

@Component({
  selector: 'app-application-status',
  templateUrl: './app-status.component.html',
  styleUrls: ['./app-status.component.css']
})
export class AppStatusComponent {
  applicationNo: string = '';
  reportData: any[] = [];

  constructor(private http: HttpClient,
    private reportService: ReportService) { }

  async filterReport() {
    if (this.applicationNo.trim()) {
      try {
        this.reportData = await this.reportService.getReport(this.applicationNo);
      } catch (error) {
        console.error('Error fetching report data:', error);
      }
    } else {
      console.warn('Please enter an Application No');
    }
  }
}
